import React from 'react';
import Slider from "react-slick";

class Recommended extends React.Component {

    state = {
        sections: [
            {
                name: 'Paris',
                price: 107,
                backgroundImg: require('../images/paris-bg.jpg')
            },
            {
                name: 'Tokyo',
                price: 87,
                backgroundImg: require('../images/tokyo-bg.jpg')
            }
        ]
    }
        sliders() {
            return this.state.sections.map(data => {
                return (
                    <li key={data}>
                        <img alt="image" src={data} />
                    </li>
                )
            });
        }
    render() {
        var settings = {
            dots: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1
          };
        return (
            <div className="container">
                <h3>Recommended for you</h3>
                <ul className="slider">

                        {this.state.sections.map ( section => 
                            <Slider {...settings}>    
                                <li className="slider-item" style={{ backgroundImage: `url(${section.backgroundImg} !important)` }}>
                                    <img src={section.backgroundImg} alt=""/>
                                    <h2>yoo</h2>
                                </li>
                            </Slider>
                        )}


                </ul>
            </div>
        )
    }
}

export default Recommended;
